import React, { useState } from "react";
import Footer from "../component/Footer";
import NavBar from "../component/NavBar";
import Filtershop from "../component/Filtershop";
import Cardshop from "../component/Cardshop";
import "../index.css"

export default function ShopPage(props) {

    const cards = [ 
        {
            title: "BackPack",
            description: "Mountainsmith Clear Creek 15",
            price: "$44.73",
            imageName: "Backpack.jpeg",
            category: "Clothing & Accessories",
        },
        {
            title: "Compass",
            description: "Suunto M-3 D Leader",
            price: "$73.00",
            imageName: "compass.jpg",
            category: "Navigation",
        },
        {
            title: "First Aid Kit",
            description: "Hart Outdoor Day Hike",
            price: "$13.95",
            imageName: "First_aid.jpeg",
            category: "First Aid & Health",
        },
        {
            title: "Water Filter",
            description: "Sawyer Mini Water Filter",
            price: "$22.95",
            imageName: "Water.jpeg",
            category: "Water filter",
        },
        {
            title: "Satellite",
            description: "Garmin inReach Mini 2",
            price: "$400",
            imageName: "satellite.jpeg",
            category: "Navigation",
        },
        {
            title: "Pass",
            description: "America the Beautiful Pass",
            price: "$79.99",
            imageName: "pass.jpeg",
            category: "Pass",
        },
        {
            title: "Trekking Poles",
            description: "Black Diamond Alpine Carbon Cork",
            price: "$199.95",
            imageName: "trekking_poles.jpg",
            category: "Clothing & Accessories",
        },
        {
            title: "Sunscreen Spray",
            description: "Sun Bum Mineral SPF 30 6 FL OZ",
            price: "$20.50",
            imageName: "spray.jpeg",
            category: "First Aid & Health",
        },
        {
            title: "Rain Pants",
            description: "REI Co-op Essential",
            price: "$59.95",
            imageName: "rain.jpeg",
            category: "Clothing & Accessories",
        },
        {
            title: "Convertible Pants",
            description: "REI Co-op Sahara",
            price: "$79.95",
            imageName: "convert.jpeg",
            category: "Clothing & Accessories",
        },
        {
            title: "Vuori Uluwatu",
            description: "2.0 Water Long-Sleeve T-Shir",
            price: "$46.00",
            imageName: "shirt.jpeg",
            category: "Clothing & Accessories",
        },
        {
            title: "Convertible Pants",
            description: "REI Co-op Sahara",
            price: "$79.95",
            imageName: "convert.jpeg",
            category: "Clothing & Accessories",
        }
    ];


    const [filter, setFilter] = useState("");
    const handleFilter = (inputText) => {
        setFilter(inputText);
    }


    return (
        <div className="ShopPage">
        <main>
            <NavBar pageName = "Shop"/>
        <section className="ShopPage_products">
            <Filtershop  
                filterSet={handleFilter}
            />
            <Cardshop  
                cards={cards.filter((card => 
                    filter 
                    ? card.category === filter
                    : true))} 
                />
        </section>
        </main>
            <Footer isInherit={true} />
        </div>
    )
}
